function initialData(e){$.ajax({url:common_url+"/searchProduct",type:"get",async:!0,data:{pageIndex:e,pageSize:12,key:$("#searchText").val()},dataType:"json",success:function(a){0<a.totleCount?(list(a.pageList),pagetion(a.totlePages,e)):($(".ul1").html("<p style='margin:10px'>未搜索到相关结果</p>"),$(".pagetion").hide())}})}function initialDatas(a){$.ajax({url:common_url+"/searchProduct",type:"get",async:!0,data:{pageIndex:a,pageSize:12,key:$("#searchText").val()},dataType:"json",success:function(a){0<a.totleCount?list(a.pageList):($(".ul1").html("<p style='margin:10px'>未搜索到相关结果</p>"),$(".pagetion").hide())}})}function list(a){for(var e="",t=0;t<a.length;t++)e+='<li><a href="productDetail.html?id='+a[t].id+'"><img src="'+img_url+a[t].defaultImage+'"/></a><div class="productName"><p><a href="productDetail.html?id='+a[t].id+'">'+a[t].modelName+" "+a[t].name+"</a></p></div></li>";$(".ul1").html(e)}function pagetion(e,a){$(".zxf_pagediv").createPage({pageNum:e,current:a,shownum:10,activepage:"current",activepaf:"nextpage",backfun:function(a){a.current>e?initialData(e):a.current<1?initialData(1):initialDatas(a.current)}})}$(function(){var a=$("#nav").offset().top;$(window).scroll(function(){$(document).scrollTop()>=a?($("#nav").addClass("fixnav"),$("#banner").addClass("new"),$("#nav .logo").addClass("logoNew")):($("#nav").removeClass("fixnav"),$("#banner").removeClass("new"),$("#nav .logo").removeClass("logoNew"))}),$(".searchButton").click(function(){initialData(1),$(".pagetion").show()})});